import tasks from "./data.js";

function dayLeftRender(task){
  var deadline = new Date(task.deadline);
  var today = new Date();
  //var todayWithoutTime = today.setHours(0,0,0); 
  //var today_time = todayWithoutTime.getTime();
  var deadline_time = deadline.getTime();
  // var time_difference = deadline_time - today_time;
  // var difference_days = time_difference / (1000*3600*24)
  console.log(today);
  return("function is called")
}

function App() {
  return (
    <div className="container-fluid background">
      <Sidebar />
      <div className="board">
        <h1>Hi Hyu!</h1>
        <p>
          Welcome back Ryu. We are glad you are here.<br></br>
          Inspire your work, leave your acheivement.
        </p>
        <button type="button" class="btn btn-dark">
          View your tasks
        </button>
        <button type="button" class="btn btn-dark">
          View timeline
        </button>
      </div>
      <div className="upcoming-tasks">
        <h1 className="upcoming-tasks-title">Upcoming tasks</h1>
        <div>
          <ul>
            {tasks.map((task, index) => {
              return (
                <div className="task-list list-border d-flex justify-content-between">
        <div className="deadline list-border m-2 p-3">{task.deadline}</div>
        <div className="content list-border m-2 p-3">{task.content}</div>
        <div className="left-day list-border m-2 p-3">{dayLeftRender(task)}</div>
    </div>
              );
            })}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default App;
