import React from "react";
import DayLeftRender from "../DayLeftRender";

function TaskBar(props) {

    function handleDelete(){
        console.log("delete function is called!!")
        props.onDelete(props.id)
    }

    function handleDone(){
      console.log("done function was called!!")
      props.onDone(props.id)
    }

  return (
    <div className="task-list list-border d-flex ">
      <div className="deadline list-border">{props.taskItem.deadline}</div>
      <div className="content list-border">{props.taskItem.content}</div>
      {DayLeftRender(props.taskItem)}
      <button onClick={handleDone} type="button" className="btn btn-info list-border done-button btn-sm">
        Done
      </button>
      <button onClick={handleDelete} type="button" className="btn btn-danger list-border delete-button btn-sm">
        Delete
      </button>
    </div>
  );
}

export default TaskBar;
