import React, { useState } from "react";

function CreateArea() {
  const [note, setNote] = useState({
    deadline: "",
    title: "",
    content: "",
  });

  function handleChange(event) {
    const { name, value } = event.target;

    setNote((prevNote) => {
      return {
        ...prevNote,
        [name]: value,
      };
    });
  }

  function submitNote(event) {
    setNote({
      deadline: "",
      title: "",
      content: "",
    });
    event.preventDefault();
  }


  return (
    <div className="container">
      <h1 className="upcoming-tasks-title">Add your new task!</h1>
      <form className="board">
        <div className="task-list list-border d-flex justify-content-between align-items-center">
        <input
              className="deadline list-border m-2 p-3"
              onChange={handleChange}
              name="date"
              value={note.deadline}
              placeholder="mm/dd"
            />
          <input
            className="content list-border m-2 p-3"
            onChange={handleChange}
            name="title"
            value={note.title}
            placeholder="title"
          />
          <div className="left-day list-border red-day m-2 p-3"> 1day left </div>
        </div>
        <textarea
          name="content"
          onChange={handleChange}
          value={note.content}
          placeholder="take a note"
          rows="3"
        ></textarea>
        <button onClick={submitNote} type="button" className="btn btn-dark">
          Add
        </button>
      </form>
    </div>
  );
}

export default CreateArea;
