import React, { useState } from "react";

function CreateArea(props) {
  const [note, setNote] = useState({
    deadline: "",
    title: "",
    content: "",
  });

  function handleChange(event) {
    const { name, value } = event.target;

    setNote((prevNote) => {
      return {
        ...prevNote,
        [name]: value,
      };
    });
    // console.log(note);
  }

  function submitNote(event) {
    props.addData(note);
    setNote({
      deadline: "",
      title: "",
      content: "",
    });
    event.preventDefault();
  }

  function renderLeftDays(note) {
    const regex = /^[0-9]{4}-([1-9]|1[0-2])-(0[1-9]|[12][0-9]|3[01])$/;
    // const today = new Date();
    // const thisYear = today.getFullYear();
    // const entered_date = thisYear.toString() + "-" + note.deadline
    if (!regex.test(note.deadline)) {
      // console.log(entered_date)
      // console.log(regex.test(entered_date))
      return <div className="expired-day list-border m-2 p-3">-----</div>;
    } else {
      console.log(note);
      // note.deadline = entered_date;
      return props.day_left(note);
    }
  }

  return (
    <div className="create-area col-sm-6 col-lg-6 p-5">
      <h1 className="title">Add your new task!</h1>
      <form className="board p-4 m-0">
        <div className="task-list list-border d-flex justify-content-between align-items-center">
          <input
            className="deadline list-border m-2 p-3"
            onChange={handleChange}
            name="deadline"
            value={note.deadline}
            placeholder="yyyy-mm-dd"
          />
          <input
            className="content list-border m-2 p-3"
            onChange={handleChange}
            name="title"
            value={note.title}
            placeholder="title"
          />
          {renderLeftDays(note)}
        </div>
        <textarea
          name="content"
          onChange={handleChange}
          value={note.content}
          placeholder="take a note"
          rows="3"
        ></textarea>
        <button onClick={submitNote} type="button" className="btn btn-dark list-border">
          Add
        </button>
      </form>
    </div>
  );
}

export default CreateArea;
