import React from "react";
import Sidebar from "../components/Sidebar.jsx";
import Upcoming from "../components/dashboard/Upcoming.jsx";
import Greeting from "../components/dashboard/Greeting.jsx";
import tasks from "../data.js";
import Today from "../components/dashboard/Today.jsx";
import CreateArea from "../components/dashboard/CreateArea.jsx";

function dayLeftRender(task) {
    const deadline = new Date(task.deadline);
    const today = new Date();
    today.setHours(0, 0, 0);
    var today_time = today.getTime();
    var deadline_time = deadline.getTime();
    var time_difference = deadline_time - today_time;
    var difference_days = Math.round(time_difference / (1000 * 3600 * 24));
    //console.log(difference_days);
    if (difference_days === 1) {
      return <div className="red-day list-border m-2 p-3">1day left</div>;
    } else if (difference_days < 7) {
      return (
        <div className="yellow-day list-border m-2 p-3">
          {difference_days + "days left"}
        </div>
      );
    } else {
      return (
        <div className="green-day list-border m-2 p-3">
          {difference_days + "days left"}
        </div>
      );
    }
  }

function Dashboard(){
return(
    <div className="container-fluid background">
      <Sidebar />
      <Greeting />
      <Upcoming 
        data={tasks}
        day_left={dayLeftRender}
      />
      <Today 
        data={tasks}
        day_left={dayLeftRender}
      />
      <CreateArea/>
    </div>
);
}

export default Dashboard;
