import tasks from "../../data";

function dayLeftRender(task) {
  var deadline = new Date(task.deadline);
  var today = new Date();
  today.setHours(0, 0, 0);
  var today_time = today.getTime();
  var deadline_time = deadline.getTime();
  var time_difference = deadline_time - today_time;
  var difference_days = Math.round(time_difference / (1000 * 3600 * 24));
  //console.log(difference_days);
  if (difference_days === 1) {
    return <div className="red-day list-border m-2 p-3">1day left</div>;
  } else if (difference_days < 7) {
    return (
      <div className="yellow-day list-border m-2 p-3">
        {difference_days + "days left"}
      </div>
    );
  } else {
    return (
      <div className="green-day list-border m-2 p-3">
        {difference_days + "days left"}
      </div>
    );
  }
}

function Upcoming() {
  return (
    <div className="upcoming-tasks ">
      <h1 className="upcoming-tasks-title">Upcoming tasks</h1>
      <div className="board p-4">
        <ul>
          {tasks.map((task, index) => {
            return (
              <div className="task-list list-border d-flex justify-content-between">
                <div className="deadline list-border m-2 p-3">
                  {task.deadline}
                </div>
                <div className="content list-border m-2 p-3">
                  {task.content}
                </div>
                {dayLeftRender(task)}
              </div>
            );
          })}
        </ul>
      </div>
    </div>
  );
}

export default Upcoming;
