import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import { AdapterMoment } from "@mui/x-date-pickers/AdapterMoment";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { DatePicker } from "@mui/x-date-pickers/DatePicker";

function CreateArea() {
  const [note, setNote] = useState({
    deadline: "",
    title: "",
    content: "",
  });

  function handleChange(event) {
    const { name, value } = event.target;

    setNote((prevNote) => {
      return {
        ...prevNote,
        [name]: value,
      };
    });
  }

  function submitNote(event) {
    setNote({
      deadline: "",
      title: "",
      content: "",
    });
    event.preventDefault();
  }

  // for day-picker
  const [value, setValue] = useState(null);

  return (
    <div className="container">
      <h1 className="upcoming-tasks-title">Add your new task!</h1>
      <form className="board">
        <div className="task-list list-border d-flex ">
          <div className="deadline list-border m-2 p-3 justify-content-between">
            <LocalizationProvider dateAdapter={AdapterMoment}>
              <DatePicker
                label="Enter day"
                value={value}
                onChange={(newValue) => {
                  setValue(newValue);
                }}
                renderInput={(params) => <TextField {...params} />}
              />
            </LocalizationProvider>
          </div>
          <input
            className="content list-border m-2 p-3"
            onChange={handleChange}
            name="title"
            value={note.title}
            placeholder="title"
          />
          <div className="content list-border red-day m-2 p-3"> 1day left </div>
        </div>
        <textarea
          name="content"
          onChange={handleChange}
          value={note.content}
          placeholder="take a note"
          rows="3"
        ></textarea>
        <button onClick={submitNote} type="button" className="btn btn-dark">
          Add
        </button>
      </form>
    </div>
  );
}

export default CreateArea;
