import tasks from "../../data";

function todayTasks(tasks) {
  var today = new Date("2023-1-30");
  const today_time = today.getTime();

  const result = tasks.filter((task) => {
    const deadline = new Date(task.deadline);
    const deadline_time = deadline.getTime();

    return today_time == deadline_time;
  });

  return result;
}

function Today() {
  return (
    <div className="upcoming-tasks ">
      <h1 className="upcoming-tasks-title">Upcoming tasks</h1>
      <div className="board p-4">
        <ul>
          {todayTasks(tasks).map((filtered_task, index) => {
            return (
              <div className="task-list list-border d-flex justify-content-between">
                <div className="deadline list-border m-2 p-3">
                  {task.deadline}
                </div>
                <div className="content list-border m-2 p-3">
                  {task.content}
                </div>
                {dayLeftRender(task)}
              </div>
            );
          })}
        </ul>
      </div>
    </div>
  );
}

export default Today;
