import Sidebar from "./Sidebar.jsx";
import Upcoming from "./dashboard/Upcoming.jsx";
import Greeting from "./dashboard/Greeting.jsx";
import tasks from "../data.js";

function todayTasks(tasks){
  var today = new Date("2023-1-30");
  const today_time = today.getTime();

  const result = tasks.filter(task=>{
    const deadline = new Date(task.deadline);
    const deadline_time = deadline.getTime();

    return today_time == deadline_time;
  });

  return result;
}

function App() {
  return (
    <div className="container-fluid background">
      <Sidebar />
      <Greeting/>
      <Upcoming/>
      {console.log(todayTasks(tasks))};
    </div>
  );
}

export default App;


